'use strict'

var Epoc = require('/node-epocjs/app');

Epoc.connect(function(event) {


    if (event.mentalCommandActionPower = 1) {

        console.log(event.mentalCommandActionPower);
        document.getElementById('d_direction').innerHTML = 'Neutral';

    }

    if (event.mentalCommandActionPower = 2) {

        console.log(event.mentalCommandActionPower);
        document.getElementById('d_direction').innerHTML = 'Push';

    }

    if (event.mentalCommandActionPower = 8) {

        console.log(event.mentalCommandActionPower);
        document.getElementById('d_direction').innerHTML = 'Lift';

    }

    if (event.mentalCommandActionPower = 4) {

        console.log(event.mentalCommandActionPower);
        document.getElementById('d_direction').innerHTML = 'Pull';
    }

    if (event.mentalCommandActionPower = 16) {

        console.log(event.mentalCommandActionPower);
        document.getElementById('d_direction').innerHTML = 'Drop';
    }

    if (event.mentalCommandActionPower = 32) {

        console.log(event.mentalCommandActionPower);
        document.getElementById('d_direction').innerHTML = 'Left';

    }

    if (event.mentalCommandActionPower = 64) {

        console.log(event.mentalCommandActionPower);
        document.getElementById('d_direction').innerHTML = 'Right';

    }

    if (event.mentalCommandActionPower = 128) {

        console.log(event.mentalCommandActionPower);
        document.getElementById('d_direction').innerHTML = 'Rotate Left';

    }

    if (event.mentalCommandActionPower = 256) {

        console.log(event.mentalCommandActionPower);
        document.getElementById('d_direction').innerHTML = 'Rotate Right';

    }

    if (event.mentalCommandActionPower = 512) {

        console.log(event.mentalCommandActionPower);
        document.getElementById('d_direction').innerHTML = 'Rotate Clockwise';

    }
});

