"use strict"

var Epoc = require('bindings')('index');

Epoc.connect(function(event) {

	if(event.blink > 0){
		console.log('blink');
		console.log(event.mentalCommandAction);
		console.log(event.mentalCommandActionPower);
		console.log("----------------------")
	}

	if(event.mentalCommandActionPower > 0){
		console.log("yo");
		console.log(event.mentalCommandActionPower);
	}

	if(event.lookingUp > 0){
		console.log('looking up');
	}

	if(event.lookingDown > 0){
		console.log('looking down');
	}

	if(event.laugh > 0){
		console.log('laugh');
	}

	if(event.smile > 0){
		console.log('smile');
	}
});
